/**
 * This component allows to edit a Materia
 */
@Component({
    selector: 'app-materia-edit',
    templateUrl: 'materia-edit.component.html',
    styleUrls: ['materia-edit.component.css']
})
export class MateriaEditComponent implements OnInit {
    item: Materia;
    model: Materia;
    formValid: Boolean;

    constructor(
    private materiaService: MateriaService,
    private route: ActivatedRoute,
    private location: Location) {
        // Init item
        this.item = new Materia();
    }

    /**
     * Init
     */
    ngOnInit() {
        this.route.params.subscribe(param => {
            const id: string = param['id'];
            if (id !== 'new') {
                this.materiaService.get(id).subscribe(item => this.item = item);
            }
            // Get relations
        });
    }


    /**
     * Save Materia
     *
     * @param {boolean} formValid Form validity check
     * @param Materia item Materia to save
     */
    save(formValid: boolean, item: Materia): void {
        this.formValid = formValid;
        if (formValid) {
            if (item._id) {
                this.materiaService.update(item).subscribe(data => this.goBack());
            } else {
                this.materiaService.create(item).subscribe(data => this.goBack());
            } 
        }
    }

    /**
     * Go Back
     */
    goBack(): void {
        this.location.back();
    }


}



